Triggers.OnInitReport:
BEGIN
  GLSetup.GET;
  //dsmh160615 Tobit ist abgeschaltet, Druck deaktiviert
  viaTobitDrucken := FALSE;
  Stationery := FALSE;
  bAbwLief := FALSE;
END;

Triggers.OnPreDataItem:
BEGIN
  CompanyInfo.GET;
  //start kpl00.cs
  Info;
  //stop kpl00.cs
  //start dsha100329
  IF CompanyInfo."MWST Nummer CH"<>'' THEN
    sMwStNrCH:=MwstNrCHText
  ELSE
    sMwStNrCH:='';
  //end dsha100329

  //dsmh131015
  IF CompanyInfo.Name = 'de Sede AG' THEN BEGIN
    TXTBLZ := '';
    TXTBIC := 'BIC';
    TXTIBAN := 'IBAN';
  END ELSE BEGIN
    TXTBLZ := 'BLZ';
    TXTBIC := '';
    TXTIBAN := '';
  END;
  //end dsmh131015
END;

Triggers.OnAfterGetRecord:
BEGIN
  //start kvs01.ae
  IF "Purchase Header"."Buy-from Vendor No." = '' THEN
    CurrReport.SKIP;
  //stop kvs01.ae

  CurrReport.LANGUAGE := Language.GetLanguageID("Language Code");

  IF RespCenter.GET("Responsibility Center") THEN BEGIN
    FormatAddr.RespCenter(CompanyAddr,RespCenter);
    CompanyInfo."Phone No." := RespCenter."Phone No.";
    CompanyInfo."Fax No." := RespCenter."Fax No.";
  END ELSE BEGIN
    FormatAddr.Company(CompanyAddr,CompanyInfo);
  END;

  DocDim1.SETRANGE("Table ID",DATABASE::"Purchase Header");
  DocDim1.SETRANGE("Document Type","Purchase Header"."Document Type");
  DocDim1.SETRANGE("Document No.","Purchase Header"."No.");

  IF "Purchaser Code" = '' THEN BEGIN
    SalesPurchPerson.INIT;
    PurchaserText := '';
  END ELSE BEGIN
    SalesPurchPerson.GET("Purchaser Code");
    PurchaserText := Text000
  END;

  IF "Your Reference" = '' THEN
    ReferenceText := ''
  ELSE
    ReferenceText := FIELDCAPTION("Your Reference");

  IF "VAT Registration No." = '' THEN
    VATNoText := ''
  ELSE
    VATNoText := FIELDCAPTION("VAT Registration No.");

  IF "Currency Code" = '' THEN BEGIN
    GLSetup.TESTFIELD("LCY Code");
    TotalText := STRSUBSTNO(Text001,GLSetup."LCY Code");
    TotalInclVATText := STRSUBSTNO(Text002,GLSetup."LCY Code");
    TotalExclVATText := STRSUBSTNO(Text006,GLSetup."LCY Code");
  END ELSE BEGIN
    TotalText := STRSUBSTNO(Text001,"Currency Code");
    TotalInclVATText := STRSUBSTNO(Text002,"Currency Code");
    TotalExclVATText := STRSUBSTNO(Text006,"Currency Code");
  END;

  FormatAddr.PurchHeaderBuyFrom(BuyFromAddr,"Purchase Header");
  IF ("Purchase Header"."Buy-from Vendor No." <> "Purchase Header"."Pay-to Vendor No.") THEN
    FormatAddr.PurchHeaderPayTo(VendAddr,"Purchase Header");
  IF "Payment Terms Code" = '' THEN
    PaymentTerms.INIT
  ELSE
    PaymentTerms.GET("Payment Terms Code");

  IF "Shipment Method Code" = '' THEN
    ShipmentMethod.INIT
  ELSE
    ShipmentMethod.GET("Shipment Method Code");

  // dsmh171005 ergänzt in Rahmenbestellung und Bestellung - Vorlage Verkaufsauftrag
  // evtl. übersetzen und belegen der Ausgabefelder für Zahlungsbedingung, Lieferbedingung und Verpackung
  // Translationtabelle Keyfelder:
  //"Tablename"           Option  1=Lieferbedingung, 2=Zahlungsbedingung, 3=Auftragsarten, 4=Verkaufsrabatte
  //                              0=Sachkonto, 1=Lieferbedingung, 2=Zahlungsbedingung,3,4,5,6,7=Verpackungsart,
  //                              8=Verkaufsrabatte,9=Einkaufsrabatte,10=Auftragsarten
  //"No./Code"            Code
  //"Language Code"       Text
  //
  sZahlungsbedingungZeile1Text := PaymentTerms.Description;
  sZahlungsbedingungZeile2Text := PaymentTerms."Description 2";
  sLieferbedingungZeile1Text:=ShipmentMethod.Description;
  sLieferbedingungZeile2Text:=ShipmentMethod."Description 2";
  IF "Purchase Header"."Language Code"> '' THEN BEGIN
      IF (sZahlungsbedingungZeile1Text > '') OR (sZahlungsbedingungZeile2Text > '')THEN BEGIN
          recTranslation.GET(2,PaymentTerms.Code,"Purchase Header"."Language Code");
          sZahlungsbedingungZeile1Text := recTranslation.Description;
          sZahlungsbedingungZeile2Text := recTranslation."Description 2";
      END;
      IF (sLieferbedingungZeile1Text > '') OR (sLieferbedingungZeile2Text > '')THEN BEGIN
          recTranslation.GET(1,ShipmentMethod.Code,"Purchase Header"."Language Code");
          sLieferbedingungZeile1Text := recTranslation.Description;
          sLieferbedingungZeile2Text := recTranslation."Description 2";
      END;

  END;


  FormatAddr.PurchHeaderShipTo(ShipToAddr,"Purchase Header");

  IF NOT CurrReport.PREVIEW THEN BEGIN
    IF ArchiveDocument THEN
      ArchiveManagement.StorePurchDocument("Purchase Header",LogInteraction);

    IF LogInteraction THEN BEGIN
      CALCFIELDS("No. of Archived Versions");
      SegManagement.LogDocument(
        13,"No.","Doc. No. Occurrence","No. of Archived Versions",DATABASE::Vendor,"Buy-from Vendor No.",
        "Purchaser Code",'',"Posting Description",'');
    END;

  END;

  //start kpl02.mr
  IF "Currency Code" <> '' THEN BEGIN
    Currency.GET("Currency Code");
    Currency.TESTFIELD("Unit-Amount Rounding Precision");
  END ELSE
    Currency.InitRoundingPrecision;
  //stop kpl02.mr

  //start kvs01.ae
  {
  //start ds01
  IF "Purchase Header"."Buy-from Vendor No." <> '' THEN
    Vendor.GET("Purchase Header"."Buy-from Vendor No.");
  //stop ds01
  }
  //stop kvs01.ae

  //
  // Setze ersten gefundenen Lagerort auf Space (bestimmt den Druck der "Liefern an"-Addresse)
  HDMErsterGefundenerLagerort := '';
END;

