Triggers.OnAfterGetRecord:
BEGIN
  IF Number = 1 THEN BEGIN
    IF NOT DocDim1.FIND('-') THEN
      CurrReport.BREAK;
  END ELSE
    IF NOT Continue THEN
      CurrReport.BREAK;

  CLEAR(DimText);
  Continue := FALSE;
  REPEAT
    OldDimText := DimText;
    IF DimText = '' THEN
      DimText := STRSUBSTNO(
        '%1 %2',DocDim1."Dimension Code",DocDim1."Dimension Value Code")
    ELSE
      DimText :=
        STRSUBSTNO(
          '%1, %2 %3',DimText,
          DocDim1."Dimension Code",DocDim1."Dimension Value Code");
    IF STRLEN(DimText) > MAXSTRLEN(OldDimText) THEN BEGIN
      DimText := OldDimText;
      Continue := TRUE;
      EXIT;
    END;
  UNTIL (DocDim1.NEXT = 0);
END;

Triggers.OnPreDataItem:
BEGIN
  //start kpl00.cs
  FirstPage := FALSE;
  //stop kpl00.cs

  CurrReport.BREAK;
END;

BEGIN
  MoreLines := PurchLine.FIND('+');
  WHILE MoreLines AND (PurchLine.Description = '') AND (PurchLine."Description 2"= '') AND
        (PurchLine."No." = '') AND (PurchLine.Quantity = 0) AND
        (PurchLine.Amount = 0) DO
    MoreLines := PurchLine.NEXT(-1) <> 0;
  IF NOT MoreLines THEN
    CurrReport.BREAK;
  PurchLine.SETRANGE("Line No.",0,PurchLine."Line No.");
  SETRANGE(Number,1,PurchLine.COUNT);
  CurrReport.CREATETOTALS(PurchLine."Line Amount",PurchLine."Inv. Discount Amount");
END;

Triggers.OnAfterGetRecord:
BEGIN
  IF Number = 1 THEN
    PurchLine.FIND('-')
  ELSE
    PurchLine.NEXT;
  "Purchase Line" := PurchLine;

  IF NOT "Purchase Header"."Prices Including VAT" AND
     (PurchLine."VAT Calculation Type" = PurchLine."VAT Calculation Type"::"Full VAT")
  THEN
    PurchLine."Line Amount" := 0;

  IF (PurchLine.Type = PurchLine.Type::"G/L Account") AND (NOT ShowInternalInfo) THEN
    "Purchase Line"."No." := '';

  //start kpl00.cs
  IF PurchLine.Indention = 0 THEN
    IndentStr := ''
  ELSE
    IndentStr := PADSTR(IndentStr, (PurchLine.Indention - 1) * 2);

  "Purchase Line".CALCFIELDS(Subtotal);
  IF "Purchase Line"."Print Price" THEN BEGIN
    PurchTotal := "Purchase Line"."Line Amount";
    PurchSubtotal := "Purchase Line".Subtotal;
    PurchDiscount := "Purchase Line"."Line Discount %";
  //start kpl02.mr
    IF "Purchase Line"."Factor (PU)" <> 1 THEN
      PurchPrice := ROUND("Purchase Line"."Direct Unit Cost" *
        "Purchase Line"."Factor (PU)" * "Purchase Line"."Qty. per Unit of Measure",
        Currency."Unit-Amount Rounding Precision")
    ELSE
  //stop kpl02.mr
      PurchPrice := "Purchase Line"."Direct Unit Cost";
  END ELSE BEGIN
    PurchTotal := 0;
    PurchSubtotal := 0;
    PurchPrice := 0;
    PurchDiscount := 0;
  END;

  //stop kpl00.cs


  //start kpl02.mr
  IF ("Purchase Line"."Factor (PU)" <> 1) AND (PurchLineForPU.GET("Purchase Line"."Document Type",
    "Purchase Line"."Document No.", "Purchase Line"."Line No.")) THEN
    "Purchase Line".Quantity := PurchLineForPU.Quantity;
  //stop kpl02.mr
END;

Triggers.OnPreSection:
BEGIN
  CurrReport.SHOWOUTPUT(NOT "Purchase Header"."Prices Including VAT" AND (VATAmount <> 0));
