    "Purchase Header"."Prices Including VAT" AND ("Purchase Header"."VAT Base Discount %" <> 0));
END;

BEGIN
  CurrReport.SHOWOUTPUT("Purchase Header"."Prices Including VAT" AND (VATAmount <> 0));
END;

