Triggers.OnAfterGetRecord:
VAR
  ItemGraphRec@1000000000 : Record 50424;
  ItemGraphMgt@1000000001 : Codeunit 50001;
  SetMgt@1000000003 : Codeunit 50005;
BEGIN
  ProdBomBool:=FALSE;

  //start kpl00.cs
  IF ("Purchase Line".Type <> "Purchase Line".Type::Item) OR (NOT "Purchase Line"."Print Line") THEN
    CurrReport.BREAK;
  //stop kpl00.cs


  //
  // Merke den ersten gefundenen Lagerort, dieser bestimmt die anhängende Lieferanschrift
  // DSMH162223 - Möglichkeit ergänzt, Lieferanschrift aus Header zu nutzen, nicht nur aus 1. Lagerort
  IF NOT bAbwLief THEN
    IF HDMErsterGefundenerLagerort <= ' ' THEN
     HDMErsterGefundenerLagerort := "Purchase Line"."Location Code";

  //
  // Lesen der Artikelkarte, um das Modell zu holen
  HDMModell:='';
  HaItemDescription:="Purchase Line".Description;

  IF ("Purchase Line".Type <> "Purchase Line".Type::Item) OR ("Purchase Line"."No."> '') THEN BEGIN
      HDMItemRecord.GET("Purchase Line"."No.");
      HDMModell:=HDMItemRecord.Model;

  // Start dsha03
      IF ("Purchase Line".Description = '') THEN
        HaItemDescription:=HDMItemRecord.Description;
  // End dsha03


     CLEAR(Artikelzeichnung);
     CLEAR(ItemGraphMgt);
     ProdBomLineTemp.DELETEALL;
     IF HDMItemRecord."Graph No." <>'' THEN BEGIN
       ItemGraphMgt.GetValiedGraphNo(HDMItemRecord."No.",Artikelzeichnung);
       IF Artikelzeichnung.Typ=Artikelzeichnung.Typ::Stückliste THEN
         ProdBomBool:=TRUE;
       SetMgt.getSet(ProdBomLineTemp,HDMItemRecord);
     END;
  END;
END;

Triggers.OnPreSection:
BEGIN
  CurrReport.SHOWOUTPUT((HDMItemRecord."No. 2" <> '') AND (COMPANYNAME = 'de Sede AG') );
END;

