Triggers.OnAfterGetRecord:
VAR
  cKeine@1000000000 : TextConst 'DEU=_KEINE';
BEGIN
  GraphExist:=FALSE;
  IF Number>1 THEN BEGIN
    IF NOT (ProdBomLineTemp.NEXT>0) THEN
      CurrReport.BREAK;
  END;
  //IF ItemRec.GET(ProdBomLineTemp."No.") THEN;

  CLEAR(Artikelzeichnung);
  CLEAR(ItemGraphMgt);
  ItemGraphMgt.GetValiedGraphNo(ProdBomLineTemp."No.",Artikelzeichnung);
  IF NOT ((Artikelzeichnung.ISEMPTY)OR(Artikelzeichnung."Zeichnungsnr."=cKeine)) THEN
    GraphExist:=TRUE;

  bZeichnungsNrChanged:=FALSE;
  IF GraphExist AND ItemGraphMgt.CheckLastPurchInvUsedGraphNo(ProdBomLineTemp."No.","Purchase Header"."Buy-from Vendor No.") THEN
    bZeichnungsNrChanged:=TRUE;
END;

Triggers.OnPreSection:
BEGIN
  CurrReport.SHOWOUTPUT( (ProdBomBool) AND ( (ProdBomLineTemp.Type=ProdBomLineTemp.Type::"Production BOM") OR
                                               (Artikelzeichnung.Typ=Artikelzeichnung.Typ::Stückliste) ) );
END;

BEGIN
  CurrReport.SHOWOUTPUT( ProdBomBool AND ( (Artikelzeichnung.Typ<>Artikelzeichnung.Typ::Stückliste) AND
                           (ProdBomLineTemp.Type<>ProdBomLineTemp.Type::"Production BOM") )
                            AND NOT (GraphExist) );
END;

BEGIN
  CurrReport.SHOWOUTPUT( ProdBomBool AND ( (Artikelzeichnung.Typ=Artikelzeichnung.Typ::Zeichnungsnummer) AND
                           (ProdBomLineTemp.Type<>ProdBomLineTemp.Type::"Production BOM") )
                            AND (GraphExist) );
END;

BEGIN
  CurrReport.SHOWOUTPUT(ProdBomBool AND ( (Artikelzeichnung.Typ=Artikelzeichnung.Typ::Dokument) AND
                           (ProdBomLineTemp.Type<>ProdBomLineTemp.Type::"Production BOM") )
                            AND (GraphExist) );
END;

BEGIN
  CurrReport.SHOWOUTPUT( NOT ProdBomBool AND ( (Artikelzeichnung.Typ=Artikelzeichnung.Typ::Zeichnungsnummer) AND
                           (ProdBomLineTemp.Type<>ProdBomLineTemp.Type::"Production BOM") )
                            AND (GraphExist) );
END;

BEGIN
  CurrReport.SHOWOUTPUT(NOT ProdBomBool AND ( (Artikelzeichnung.Typ=Artikelzeichnung.Typ::Dokument) AND
                           (ProdBomLineTemp.Type<>ProdBomLineTemp.Type::"Production BOM") )
                            AND (GraphExist) );
END;

