  //
  // Andruck der Ursprungsinformationen nur, wenn vorhanden
  IF ("Purchase Header"."Ursprung. Auftragsnr." <> '') AND
     ("Purchase Header"."Ursprung. Mandant" <> '') THEN BEGIN
       CurrReport.SHOWOUTPUT := TRUE
  END ELSE BEGIN
      CurrReport.SHOWOUTPUT := FALSE
  END;
  // dshdm050303-Ende
END;

BEGIN
  //
  //  Lieferdatum nur drucken, wenn Zeilenart = Artikel, G/L Account etc.

  CurrReport.SHOWOUTPUT(("Purchase Line".Type = "Purchase Line".Type::Item) OR
                        ("Purchase Line".Type = "Purchase Line".Type::"G/L Account") OR
                        ("Purchase Line".Type = "Purchase Line".Type::"Fixed Asset"));
END;

Triggers.OnPreDataItem:
BEGIN
  //mh1.tb.01 --- Start---
  IF KreditorenEinrichtung.GET THEN
    IF NOT  KreditorenEinrichtung."Belegrabatte drucken" THEN
      CurrReport.BREAK;
  //mh1.tb.01 --- Stop  ---
END;

Triggers.OnAfterGetRecord:
BEGIN
  //mh1.tb.01 --- Start ---
  CALCFIELDS("Einkauf Belegrabatte".Rabattbeschreibung);
  //mh1.tb.01 --- Stop ---
END;

Triggers.OnPreDataItem:
BEGIN
  IF VATAmount = 0 THEN
    CurrReport.BREAK;

  IF VATAmountLine.COUNT = 1 THEN
    CurrReport.BREAK;

  SETRANGE(Number,1,VATAmountLine.COUNT);
  CurrReport.CREATETOTALS(
    VATAmountLine."Line Amount",VATAmountLine."Inv. Disc. Base Amount",
    VATAmountLine."Invoice Discount Amount",VATAmountLine."VAT Base",VATAmountLine."VAT Amount");
END;

Triggers.OnAfterGetRecord:
BEGIN
  VATAmountLine.GetLine(Number);
END;

Triggers.OnPreDataItem:
BEGIN
  IF "Purchase Header"."Buy-from Vendor No." = "Purchase Header"."Pay-to Vendor No." THEN
    CurrReport.BREAK;
END;

Triggers.OnPreSection:
BEGIN
  CurrReport.SHOWOUTPUT("Purchase Header"."Pay-to Vendor No."> ' ');
END;

Triggers.OnPreDataItem:
BEGIN
  IF  HDMErsterGefundenerLagerort > ' ' THEN BEGIN
      HDMLagerortRecord.GET(HDMErsterGefundenerLagerort);
     //
     // Andrucken der allgemeinen Anschrift aus dem Debitorenstamm
     CLEAR(ShipToAddr);
     i := 1;
     IF HDMLagerortRecord."Name 2"> ' ' THEN BEGIN
        ShipToAddr[i] := HDMLagerortRecord."Name 2";
        i := i+1;
     END;
     IF HDMLagerortRecord.Name > ' ' THEN BEGIN
        ShipToAddr[i] := HDMLagerortRecord.Name;
        i := i+1;
     END;
     IF HDMLagerortRecord.Address > ' ' THEN BEGIN
        ShipToAddr[i] := HDMLagerortRecord.Address;
        i := i+1;
     END;
     IF HDMLagerortRecord."Address 2"> ' ' THEN BEGIN
        ShipToAddr[i] := HDMLagerortRecord."Address 2";
        i := i+1;
     END;
     IF (HDMLagerortRecord."Country/Region Code"> ' ') OR
        (HDMLagerortRecord."Post Code"> ' ') OR
        (HDMLagerortRecord.City > ' ') THEN BEGIN
         ShipToAddr[i] := STRSUBSTNO(HDMTextLandPlzOrt,HDMLagerortRecord."Country/Region Code",
                                     HDMLagerortRecord."Post Code",
                                     HDMLagerortRecord.City);
     END;
  END;

  IF (ShipToAddr[1] = '') THEN
      CurrReport.BREAK;
END;

Triggers.OnAfterGetRecord:
BEGIN
  //start kpl00.cs
  IF NOT "Footer Text"."Order Print" THEN
    CurrReport.SKIP;
  //stop kpl00.cs
END;

Triggers.OnPreSection:
BEGIN
  //start dshh01
  CurrReport.SHOWOUTPUT(viaTobitDrucken = TRUE);
  //stop  dshh01
END;

Triggers.OnPreDataItem:
BEGIN
  //Gefiltert auf "Extended Text Header"."No." = EK-Allg.
END;

Triggers.OnAfterGetRecord:
BEGIN
  IF (FORMAT("Ending Date")<>'') THEN BEGIN
    IF NOT(("Starting Date" <= TODAY) AND ("Ending Date">= TODAY)) THEN
      CurrReport.SKIP;
  END ELSE BEGIN
    IF NOT("Starting Date" <= TODAY) THEN
      CurrReport.SKIP;
  END;
END;

Triggers.OnOpenForm:
BEGIN
  ArchiveDocument := ArchiveManagement.SalesDocArchiveGranule;
  LogInteraction := SegManagement.FindInteractTmplCode(13) <> '';

  RequestOptionsForm.ArchiveDocument.ENABLED(ArchiveDocument);
  RequestOptionsForm.LogInteraction.ENABLED(LogInteraction);
  bAbwLief:=FALSE;
END;

Triggers.OnValidate:
BEGIN
  IF NOT ArchiveDocument THEN
    LogInteraction := FALSE;
END;

BEGIN
  IF LogInteraction THEN
    ArchiveDocument := RequestOptionsForm.ArchiveDocument.ENABLED;
END;

