PROCEDURE Info@5025850();
VAR
  Management@1105000002 : Record 5048859;
BEGIN
  //start kpl00.cs
  SenderWindow := CompanyInfo.Name + '   ' + CompanyInfo.Address + '   ' + CompanyInfo."Country/Region Code" + '-'
                  + CompanyInfo."Post Code" + ' ' + CompanyInfo.City+
                 ' '+CompanyInfo.County;

  IF NOT Stationery THEN BEGIN
     CompanyInfo.CALCFIELDS(CompanyInfo.Picture);
     Line :=
  '______________________________________________________________________________________________________________________________'
  END;

  i := 1;
  IF Management.FIND('-') THEN BEGIN
    REPEAT
      ManagementText[i] := Management.Name;
      i := i+1;
    UNTIL (Management.NEXT = 0) OR (i=3);
  END;
  //stop kpl00.cs
END;

